let input = msg.elapsed.time;
// Extract values
let days = input.days;
let hours = input.hours;
let minutes = input.minutes;
let seconds = Math.floor(input.seconds + (input.milliseconds / 1000)); // Convert milliseconds to seconds

// Format the string DD:HH:MM:SS
let result = `${String(days).padStart(2, '0')}:${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;

// Set the result back to the msg object
